syntax:-   $(selector).action();

$(document).readyy(function() {
    $('[attribute]').action();
});
// accessing HTML element:- "p", "button", "h4"....
//            by id:-       #id    ==> #click
//            by class:-    .class ==> .btn ,btn-default
//            attribute:-   "[]"  ==> "[href]", "[data-toggle="tooltip"]"
//current elements          $(this).


